mapper.sql:
SELECT a.*
FROM bgm_receive_management a


        AND a.id = #{param.id}


        AND a.management_number LIKE CONCAT('%', #{param.managementNumber}, '%')


        AND a.agreement_number LIKE CONCAT('%', #{param.agreementNumber}, '%')


        AND a.vendor_id = #{param.vendorId}


        AND a.sm_id = #{param.smId}


        AND a.fleeceTime LIKE CONCAT('%', #{param.fleecetime}, '%')


        AND a.collectedWeight = #{param.collectedweight}


        AND a.warehousingWeight = #{param.warehousingweight}


        AND a.collectedNumber LIKE CONCAT('%', #{param.collectednumber}, '%')


        AND a.claspWeight = #{param.claspweight}


        AND a.storageBin LIKE CONCAT('%', #{param.storagebin}, '%')


        AND a.peelingTime LIKE CONCAT('%', #{param.peelingtime}, '%')


        AND a.grossWeight = #{param.grossweight}


        AND a.tareWeight = #{param.tareweight}


        AND a.netWeight = #{param.netweight}


        AND a.transportVehicle LIKE CONCAT('%', #{param.transportvehicle}, '%')


        AND a.supplyNumber = #{param.supplynumber}


        AND a.over_haired_personnel LIKE CONCAT('%', #{param.overHairedPersonnel}, '%')


        AND a.over_skinned_personnel LIKE CONCAT('%', #{param.overSkinnedPersonnel}, '%')


        AND a.cancellation LIKE CONCAT('%', #{param.cancellation}, '%')


        AND a.remark LIKE CONCAT('%', #{param.remark}, '%')


        AND a.management_status = #{param.managementStatus}
